export const experiments = function (...params) {
    if (typeof params[0] === `object`) {
        const experimentsObject = params[0];
        this.hooks.on(`build.experiments`, (experiments = {}) => ({
            ...experiments,
            ...experimentsObject,
        }));
    }
    else {
        this.hooks.on(`build.experiments`, (experiments = {}) => ({
            ...experiments,
            [`${params[0]}`]: params[1],
        }));
    }
    return this;
};
