import type { Modules } from '@roots/bud-framework';
/**
 * A decorator that adds a `label` property to the class.
 *
 * @param label - Extension label
 */
export declare const label: (label: `${keyof Modules & string}`) => <Type extends new (...args: any[]) => any>(constructor: Type) => {
    new (...args: any[]): {
        [x: string]: any;
        label: string;
    };
} & Type;
