import { realpath } from 'node:fs/promises';
import { join } from 'node:path';
export const exists = async (...path) => {
    let resolvedPath;
    try {
        resolvedPath = await realpath(join(...path));
    }
    catch (error) {
        return false;
    }
    return typeof resolvedPath === `string` ? true : false;
};
