import isString from '@roots/bud-support/isString';
const name = label => label
    .map(label => `${label.charAt(0).toUpperCase()}${label.slice(1)}`)
    .join(` `);
export const transformEntry = (slug, color) => ({
    color: color.toLowerCase().replace(/<alpha-value>/g, `1`),
    name: name(slug),
    slug: slug.join(`-`).toLowerCase(),
});
export const toWordPressEntries = ([entry, path]) => {
    const [name, value] = entry;
    if (!isString(value)) {
        const result = Object.entries(value)
            .map(i => [i, [...path, name]])
            .flatMap(toWordPressEntries);
        return result;
    }
    return [transformEntry([...path, name], value)];
};
export const transform = palette => Object.entries(palette)
    .map(i => [i, []])
    .flatMap(toWordPressEntries);
