import * as api from '@roots/wordpress-hmr/filter';
export const enforceNamespace = (id, name) => id.startsWith(name) ? id : `${name}/${id}`;
export const filterCallback = (filters, namespace, handle) => {
    if (!filters || !namespace || !handle)
        return;
    Object.entries(filters).map(([hook, records]) => Object.entries(records).map(([name, callback]) => {
        name = enforceNamespace(name, namespace);
        handle({ callback, hook, name }, api);
    }));
};
